
# ------------------------------------------------------------------------------
# Dependency
# ------------------------------------------------------------------------------

class Dependency( object ):

  def __getattr__( s, name ):
    return Get_attr( s, name = name )
  
  def __getitem__( s, name ):
    return Get_item( s, name = name )

# ------------------------------------------------------------------------------
# Get_attr
# ------------------------------------------------------------------------------

class Get_attr( Dependency ):
  
  def __init__( s, src, name ):
    s.src = src
    s.name = name

# ------------------------------------------------------------------------------
# Get_item
# ------------------------------------------------------------------------------

class Get_item( Dependency ):
  
  def __init__( s, src, name ):
    s.src = src
    s.name = name

