
# ------------------------------------------------------------------------------
# image__html
# ------------------------------------------------------------------------------

def image__html( image ):
  '''
  image = dict( url = '', title = '' )
  '''  
  return IMAGE_HTML % image

# --------------------------------------

IMAGE_HTML = u'''
<div style='text-align: center'>
  <div>%(title)s</div>
  <img src='%(url)s'></img>
</div>
'''

# ------------------------------------------------------------------------------
# body__page
# ------------------------------------------------------------------------------

def body__page( body ):
  return PAGE_HTML % dict(
    body = body
  )
  
# --------------------------------------

PAGE_HTML = u'''
<html>
  <head>
  </head>
  <body>
    %(body)s
  </body>
</html>
'''

