
function AsvAdminInitSortable(SID,url){if(SID[0]!='#'){SID='#'+SID;}
$(SID).sortable({items:'div.sortable',start:function(event,ui){$('img',ui.item).toggle();$(ui.item).css('opacity',50);},stop:function(event,ui){$('img',ui.item).toggle();$(ui.item).css('opacity',100);},update:function(event,ui){var a=$(SID).sortable('toArray');var m=$('input.asv_img_model_mark',SID).val()
var ee={'mark':m,'order':a}
ee=$.toJSON(ee);$.post(url,{csrfmiddlewaretoken:$('input[name=csrfmiddlewaretoken]').val(),reorder:ee},function(data,textStatus,jqXHR){},'json');},opacity:0.6,placeholder:"ui-state-highlight"});}