import wtforms


class SubmitStartForm(wtforms.Form):
    title = wtforms.TextField(
        'Title',
        [wtforms.validators.Length(min=0, max=500)])
    description = wtforms.TextAreaField('Description of this work')
    file = wtforms.FileField('File')
